            Skip();

            if(Accept(DATA_GSOBJECT_BEGIN_TOKEN))
            {
                Skip();

                Expect(DATA_GSBOOL_BEGIN_TOKEN, DATA_GSDATE_BEGIN_TOKEN, DATA_GSINT_BEGIN_TOKEN,
                       DATA_GSREAL_BEGIN_TOKEN);

