                {
                    string byteString = dataString.Substring(i * 2, 2);
                    int    byteValue  = Convert.ToInt32(byteString, 16);
                    bytes[i] = (byte)byteValue;
                }

                obj = new NSData(bytes);

                //skip end token
                Skip();
            }

            return obj;
        }

